// Rate limiting check
const currentTime = Date.now();
const rateLimitWindow = 60000; // 1 minute
const maxRequestsPerWindow = 10;

// Get client IP or user identifier
const clientId = $input.first().json.headers['x-forwarded-for'] || 
                $input.first().json.headers['x-real-ip'] || 
                $input.first().json.headers['cf-connecting-ip'] || 
                'anonymous';

// Simple in-memory rate limiting (in production, use Redis)
if (!global.rateLimitStore) {
  global.rateLimitStore = {};
}

if (!global.rateLimitStore[clientId]) {
  global.rateLimitStore[clientId] = [];
}

// Clean old entries
global.rateLimitStore[clientId] = global.rateLimitStore[clientId].filter(
  timestamp => currentTime - timestamp < rateLimitWindow
);

// Check if rate limit exceeded
if (global.rateLimitStore[clientId].length >= maxRequestsPerWindow) {
  return {
    rateLimitExceeded: true,
    clientId,
    requestCount: global.rateLimitStore[clientId].length,
    resetTime: Math.min(...global.rateLimitStore[clientId]) + rateLimitWindow
  };
}

// Add current request
global.rateLimitStore[clientId].push(currentTime);

return {
  rateLimitExceeded: false,
  clientId,
  requestCount: global.rateLimitStore[clientId].length,
  ...($input.first().json)
};

// Enhanced prompt processing and API configuration
const originalPrompt = $input.first().json.query.message;
const style = $input.first().json.query.style || 'photorealistic';
const size = $input.first().json.query.size || '1024x1024';
const seed = $input.first().json.query.seed || Math.floor(Math.random() * 1000000);
const model = $input.first().json.query.model || 'flux';
const quality = $input.first().json.query.quality || 'high';

// Content filtering - basic inappropriate content detection
const inappropriateTerms = ['nude', 'naked', 'nsfw', 'explicit', 'sexual', 'porn', 'xxx'];
const containsInappropriate = inappropriateTerms.some(term => 
  originalPrompt.toLowerCase().includes(term.toLowerCase())
);

if (containsInappropriate) {
  return {
    error: 'Content policy violation',
    message: 'The prompt contains inappropriate content that cannot be processed.',
    code: 'CONTENT_VIOLATION'
  };
}

// Enhanced prompt based on style with better descriptions
let enhancedPrompt = originalPrompt;
let styleModifiers = '';

switch(style.toLowerCase()) {
  case 'artistic':
    styleModifiers = 'oil painting, masterpiece, highly detailed, vibrant colors, professional artwork, fine art';
    break;
  case 'photorealistic':
    styleModifiers = 'photorealistic, high resolution, professional photography, 8k, ultra-detailed, sharp focus';
    break;
  case 'cartoon':
    styleModifiers = 'cartoon style, animated, colorful, Disney-like, cel-shaded, illustration';
    break;
  case 'cyberpunk':
    styleModifiers = 'cyberpunk style, neon lights, futuristic, dark atmosphere, sci-fi, digital art';
    break;
  case 'fantasy':
    styleModifiers = 'fantasy art, magical, mystical, epic, detailed fantasy illustration';
    break;
  case 'minimalist':
    styleModifiers = 'minimalist, clean, simple, elegant, modern design';
    break;
  case 'vintage':
    styleModifiers = 'vintage style, retro, classic, aged, nostalgic';
    break;
  default:
    styleModifiers = 'high quality, detailed, professional';
}

// Quality modifiers
let qualityModifiers = '';
switch(quality.toLowerCase()) {
  case 'high':
    qualityModifiers = 'best quality, ultra detailed, 4k, hdr';
    break;
  case 'medium':
    qualityModifiers = 'good quality, detailed';
    break;
  case 'low':
    qualityModifiers = 'standard quality';
    break;
}

enhancedPrompt = `${originalPrompt}, ${styleModifiers}, ${qualityModifiers}`;

// Parse size
const [width, height] = size.split('x').map(Number);

// Validate dimensions
if (width > 2048 || height > 2048 || width < 256 || height < 256) {
  return {
    error: 'Invalid dimensions',
    message: 'Image dimensions must be between 256x256 and 2048x2048',
    code: 'INVALID_DIMENSIONS'
  };
}

// Multiple API providers for better reliability
const apiConfigs = [
  {
    name: 'Pollinations AI - Flux',
    url: `https://image.pollinations.ai/prompt/${encodeURIComponent(enhancedPrompt)}?model=flux&width=${width}&height=${height}&seed=${seed}&enhance=true`,
    priority: 1
  },
  {
    name: 'Pollinations AI - Turbo',
    url: `https://image.pollinations.ai/prompt/${encodeURIComponent(enhancedPrompt)}?model=turbo&width=${width}&height=${height}&seed=${seed}`,
    priority: 2
  },
  {
    name: 'Pollinations AI - Default',
    url: `https://image.pollinations.ai/prompt/${encodeURIComponent(enhancedPrompt)}?width=${width}&height=${height}&seed=${seed}`,
    priority: 3
  }
];

return {
  originalPrompt,
  enhancedPrompt,
  style,
  size,
  width,
  height,
  seed,
  model,
  quality,
  apiConfigs,
  currentApiIndex: 0,
  retryCount: 0,
  maxRetries: 3,
  timestamp: new Date().toISOString(),
  requestId: `img_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`
};

// Check if the HTTP request failed
const inputData = $input.first();
const hasError = inputData.json.error !== undefined;
const statusCode = inputData.json.statusCode || inputData.json.code;

if (hasError || statusCode >= 400) {
  // Determine error type
  let errorType = 'unknown';
  let errorMessage = inputData.json.message || 'Unknown error';

  if (statusCode === 408 || errorMessage.includes('timeout')) {
    errorType = 'timeout';
  } else if (statusCode >= 500) {
    errorType = 'server_error';
  } else if (statusCode >= 400) {
    errorType = 'client_error';
  }

  return {
    hasError: true,
    errorType,
    statusCode,
    errorMessage,
    originalData: inputData.json
  };
}

return {
  hasError: false,
  statusCode: 200,
  originalData: inputData.json
};

// Retry logic with exponential backoff
const inputData = $input.first().json;
const currentApiIndex = inputData.currentApiIndex || 0;
const retryCount = inputData.retryCount || 0;
const maxRetries = inputData.maxRetries || 3;
const apiConfigs = inputData.apiConfigs || [];

// Check if we can retry
const canRetry = retryCount < maxRetries && currentApiIndex < apiConfigs.length;

if (canRetry) {
  // Calculate exponential backoff delay (in ms)
  const baseDelay = 1000; // 1 second
  const backoffDelay = baseDelay * Math.pow(2, retryCount);

  // Determine next API to try
  const nextApiIndex = (currentApiIndex + 1) % apiConfigs.length;
  const nextRetryCount = nextApiIndex === 0 ? retryCount + 1 : retryCount;

  return {
    shouldRetry: true,
    nextApiIndex,
    nextRetryCount,
    backoffDelay,
    currentApiIndex,
    retryCount,
    maxRetries,
    apiConfigs,
    originalPrompt: inputData.originalPrompt,
    enhancedPrompt: inputData.enhancedPrompt,
    style: inputData.style,
    size: inputData.size,
    width: inputData.width,
    height: inputData.height,
    seed: inputData.seed,
    model: inputData.model,
    quality: inputData.quality,
    requestId: inputData.requestId,
    timestamp: inputData.timestamp,
    lastError: inputData.errorMessage || 'Unknown error',
    lastStatusCode: inputData.statusCode
  };
}

// Cannot retry anymore
return {
  shouldRetry: false,
  currentApiIndex,
  retryCount,
  maxRetries,
  apiConfigs,
  originalPrompt: inputData.originalPrompt,
  enhancedPrompt: inputData.enhancedPrompt,
  requestId: inputData.requestId,
  timestamp: inputData.timestamp,
  lastError: inputData.errorMessage || 'Unknown error',
  lastStatusCode: inputData.statusCode,
  error: 'All retry attempts exhausted'
};

